import React, { useContext } from 'react';
import UserContext from '../context/UserContext';

const ChangeUser = () => {
  const { userName, changeUserName } = useContext(UserContext);

  const handleInputChange = (e) => {
    // Add any necessary validation or checks here
    const newUserName = e.target.value;
    changeUserName(newUserName);
  };

  const handleResetUser = () => {
    // Example: Resetting user name to a default value
    const defaultUserName = 'Default User';
    changeUserName(defaultUserName);
  };

  return (
    <>
      <input onChange={handleInputChange} type="text" />
      <button onClick={handleResetUser}>Reset User</button>
    </>
  );
};

export default ChangeUser;
