import { useState } from 'react';
import './App.css';
import User from './components/User';
import UserContext from './context/UserContext';

function App() {
  const [user, setUser] = useState('Taras');
  return (
    <UserContext.Provider>
      <div className="App">
        <User user={user} />
      </div>
    </UserContext.Provider>
  );
}

export default App;
